            "target_date_pattern":"yyyy-MM-dd HH:mm:ss,SSS"
          }
         }
       }
     },
    {
      "filter":"keyvalue",
      "sort_order":1,
      "conditions":{
        "fields":{
          "type":[
            "hdfs_audit"
          ]
         }
       },
      "source_field":"log_message",
      "value_split":"=",
      "field_split":"\t",
      "post_map_values":{
