            "new_fieldname":"resource"
          }
         },
        "ip":{
          "map_fieldname":{
            "new_fieldname":"cliIP"
          }
         },
        "allowed":[
          {
            "map_fieldvalue":{
              "pre_value":"true",
              "post_value":"1"
            }
           },
          {
            "map_fieldvalue":{
              "pre_value":"false",
              "post_value":"0"
            }
           },
          {
            "map_fieldname":{
              "new_fieldname":"result"
            }
           }
         ],
        "cmd":{
          "map_fieldname":{
            "new_fieldname":"action"
          }
         },
        "proto":{
          "map_fieldname":{
            "new_fieldname":"cliType"
          }
         },
        "callerContext":{
          "map_fieldname":{
            "new_fieldname":"req_caller_id"
          }
         }
       }
     },
    {
      "filter":"grok",
      "sort_order":2,
      "source_field":"ugi",
      "remove_source_field":"false",
      "conditions":{
        "fields":{
          "type":[
            "hdfs_audit"
          ]
         }
       },
      "message_pattern":"%{USERNAME:p_user}.+auth:%{USERNAME:p_authType}.+via %{USERNAME:k_user}.+auth:%{USERNAME:k_authType}|%{USERNAME:user}.+auth:%{USERNAME:authType}|%{USERNAME:x_user}",
      "post_map_values":{
        "user":{
          "map_fieldname":{
            "new_fieldname":"reqUser"
          }
         },
        "x_user":{
          "map_fieldname":{
            "new_fieldname":"reqUser"
          }
         },
        "p_user":{
          "map_fieldname":{
            "new_fieldname":"reqUser"
          }
         },
        "k_user":{
          "map_fieldname":{
            "new_fieldname":"proxyUsers"
          }
         },
        "p_authType":{
          "map_fieldname":{
            "new_fieldname":"authType"
          }
         },
        "k_authType":{
          "map_fieldname":{
